import assert from 'assert';
import {iter} from '@iterable-iterator/iter';
import {consume} from '@iterable-iterator/consume';

/**
 * Drops the first <code>n</code> values of the input iterable.
 *
 * @example
 * // returns [ 3 , 4 ]
 * list( _drop( range( 5 ) , 3 ) ) ;
 *
 * @param {Iterable} iterable - The input iterable.
 * @param {Number} n - The nonnegative number of values to drop.
 * @returns {IterableIterator} - The remaining values of the input iterable.
 */
export default function* _drop(iterable, n) {
	assert(Number.isInteger(n) && n >= 0);
	const iterator = iter(iterable);
	consume(iterator, n);
	yield* iterator;
}

import assert from 'assert';
import {deque} from '@data-structure/deque';

/**
 * Returns the last <code>n</code> values of the input iterable in an array.
 *
 * @example
 * // returns [ 3 , 4 ]
 * list( _tail( range( 5 ) , 2 ) ) ;
 *
 * @param {Iterable} iterable - The input iterable.
 * @param {Number} n - The nonnegative number of values to include in the output.
 * @returns {IterableIterator} - The last <code>n</code> values of the input iterable.
 */
export default function* _tail(iterable, n) {
	assert(Number.isInteger(n) && n >= 0);
	yield* deque(iterable, n);
}

import assert from 'assert';
import {iter} from '@iterable-iterator/iter';

/**
 * Yields the first <code>n</code> elements of the input iterable.
 *
 * @example
 * // returns [ 0 , 1 , 2 ]
 * list( _take( range( 5 ) , 3 ) ) ;
 *
 * @param {Iterable} iterable - The input iterable.
 * @param {Number} n - The nonnegative number of elements to include in the output.
 * @returns {IterableIterator} - The first <code>n</code> elements of the input iterable.
 */
export default function* _take(iterable, n) {
	assert(Number.isInteger(n) && n >= 0);

	const iterator = iter(iterable);

	while (n-- > 0) {
		const current = iterator.next();

		if (current.done) {
			return;
		}

		yield current.value;
	}
}

import assert from 'assert';
import {deque} from '@data-structure/deque';
import {iter} from '@iterable-iterator/iter';

/**
 * Yields all elements of the iterable except the last <code>n</code> ones.
 *
 * @example
 * // returns [ 0 , 1 , 2 ]
 * list( _trunc( range( 5 ) , 2 ) ) ;
 *
 * @param {Iterable} iterable - The input iterable.
 * @param {Number} n - The nonnegative number of elements to exclude from the output.
 * @returns {IterableIterator}
 */
export default function* _trunc(iterable, n) {
	assert(Number.isInteger(n) && n >= 0);

	if (n === 0) {
		yield* iterable;
		return;
	}

	const iterator = iter(iterable);

	const buffer = deque(null, n);

	while (n-- > 0) {
		const event = iterator.next();
		if (event.done) {
			return;
		}

		buffer.append(event.value);
	}

	for (const value of iterator) {
		yield buffer.popleft();
		buffer.append(value);
	}
}

import _drop from './_drop.js';
import _tail from './_tail.js';

/**
 * Drops the first <code>n</code> values of the input iterable.
 * If <code>n</code> is negative, behaves like <code>{@link tail}( iterable,
 * -n)</code>.
 *
 * @example
 * // returns [ 3 , 4 ]
 * list( drop( range( 5 ) , 3 ) ) ;
 *
 * @param {Iterable} iterable - The input iterable.
 * @param {Number} n - The number of values to drop.
 * @returns {IterableIterator} - The remaining values of the input iterable.
 */
export default function drop(iterable, n) {
	return n < 0 ? _tail(iterable, -n) : _drop(iterable, n);
}

import {iter} from '@iterable-iterator/iter';

/**
 * Drop elements of the input iterable while the current element satisfies the
 * input predicate.
 *
 * @param {Function} predicate - The input predicate.
 * @param {Iterable} iterable - The input iterable.
 * @returns {Iterator}
 */
export default function* dropwhile(predicate, iterable) {
	const iterator = iter(iterable);

	for (const item of iterator) {
		if (predicate(item)) {
			continue;
		}

		// Equivalent to return chain( [ [ item ] , iterator ] ) ;

		yield item;

		yield* iterator;

		return;
	}
}

import take from './take.js';

/**
 * Same as {@link take}.
 * @function head
 */
const head = take;
export default head;

export {default as _drop} from './_drop.js';
export {default as _tail} from './_tail.js';
export {default as _take} from './_take.js';
export {default as _trunc} from './_trunc.js';
export {default as drop} from './drop.js';
export {default as dropwhile} from './dropwhile.js';
export {default as head} from './head.js';
export {default as slice} from './slice.js';
export {default as tail} from './tail.js';
export {default as take} from './take.js';
export {default as takewhile} from './takewhile.js';
export {default as trunc} from './trunc.js';

import {iter} from '@iterable-iterator/iter';

/**
 * Same as
 * <code>map( [ i , x ] => x , filter( [ i , x ] => (new Set( range( start ,
 * stop , step ) ) ).has( i ) , enumerate( iterable ) ) );</code>.
 *
 * @param {Iterable} iterable - The input iterable.
 * @param {Number} start - Where to start the slice.
 * @param {Number} stop - Where to stop the slice.
 * @param {Number} step - The step of the slice.
 * @returns {IterableIterator}
 */
export default function* slice(iterable, start, stop, step) {
	const iterator = iter(iterable);

	while (start > 0) {
		if (iterator.next().done) {
			return;
		}

		--start;
		--stop;
	}

	while (stop > 0) {
		const current = iterator.next();

		if (current.done) {
			return;
		}

		yield current.value;

		--stop;

		let n = step;

		while (n > 1) {
			if (iterator.next().done) {
				return;
			}

			--n;
		}
	}
}

import _drop from './_drop.js';
import _tail from './_tail.js';

/**
 * Returns the last <code>n</code> values of the input iterable in an array.
 * If <code>n</code> is negative, behaves like <code>{@link drop}( iterable,
 * -n)</code>.
 *
 * @example
 * // returns [ 3 , 4 ]
 * list( tail( range( 5 ) , 2 ) ) ;
 *
 * @param {Iterable} iterable - The input iterable.
 * @param {Number} n - The number of values to include in the output.
 * @returns {IterableIterator} - The last <code>n</code> values of the input iterable.
 */
export default function tail(iterable, n) {
	return n < 0 ? _drop(iterable, -n) : _tail(iterable, n);
}

import _trunc from './_trunc.js';
import _take from './_take.js';

/**
 * Yields the first <code>n</code> elements of the input iterable. If
 * <code>n</code> is negative, behaves like <code>{@link trunc}( iterable,
 * -n)</code>.
 *
 * @example
 * // returns [ 0 , 1 , 2 ]
 * list( take( range( 5 ) , 3 ) ) ;
 *
 * @param {Iterable} iterable - The input iterable.
 * @param {Number} n - The number of elements to include in the output.
 * @returns {IterableIterator} - The first <code>n</code> elements of the input iterable.
 */
export default function take(iterable, n) {
	return n < 0 ? _trunc(iterable, -n) : _take(iterable, n);
}

/**
 * Output elements of the input iterable while the current element satisfies the
 * input predicate.
 *
 * @param {Function} predicate - The input predicate.
 * @param {Iterable} iterable - The input iterable.
 * @returns {Iterator}
 */
export default function* takewhile(predicate, iterable) {
	for (const item of iterable) {
		if (!predicate(item)) {
			return;
		}

		yield item;
	}
}

import _take from './_take.js';
import _trunc from './_trunc.js';

/**
 * Yields all elements of the iterable except the last <code>n</code> ones. If
 * <code>n</code> is negative, behaves like <code>{@link take}( iterable, -n
 * )</code>.
 *
 * @example
 * // returns [ 0 , 1 , 2 ]
 * list( trunc( range( 5 ) , 2 ) ) ;
 *
 * @param {Iterable} iterable - The input iterable.
 * @param {Number} n - The number of elements to exclude from the output.
 * @returns {IterableIterator}
 */
export default function trunc(iterable, n) {
	return n < 0 ? _take(iterable, -n) : _trunc(iterable, n);
}

